import React, { useEffect, useState } from 'react'
// import cctu from '../images/cctu.jpg';
import '../styles/Homepage.css';
// import cctu_src_ec from '../images/cctu_src_ec.jpeg'
import Signin from '../components/Signin';
import { Button } from 'react-bootstrap';

const HomePage = () => {
  const [info, setInfo] = useState({});

  useEffect(()=>{
    fetch('http://localhost:5000/home', {
      method: 'GET',
      headers: {'Content-Type':'application/json'},
    })
    .then(res=>res.json())
    .then(res=>{
      if (res.status==='success'){
        setInfo(res.data);
      }
    })
    .catch(err=>console.log(err))
  },[])

  const handleLogOut=()=>{
    fetch('http://localhost:5000/signout', {
      method: 'GET'
    })
    .then(res=>res.json())
    .then(res=>{
      if (res.status==='success'){
        window.location.href = '/'
      }
    })
    .catch(err=>console.log(err))
  }

  console.log(info);

  return (  
    <div className='home_display d-flex flex-column align-items-center min-vh-100'>
        <h1>Welcome to UniGhana!</h1>
        <h3>{info.firstname} {info.lastname}</h3>
        <Button onClick={handleLogOut}>Log Out</Button>
    </div>
  )
}


export default HomePage
