import React, { useState } from 'react'
import { Button, Form } from 'react-bootstrap'
import '../styles/SignUp.css';


const SignUp = () => {
  const [validated, setValidated] = useState(false);
  const [userInfo, setUserInfo] = useState({
    firstname: '', lastname: '', email: '',
    password:'', confirmPassword: ''
  });

  const handleSubmit=(event)=>{
    const form = event.currentTarget;
    if (form.checkValidity()===false){
      event.preventDefault();
      event.stopPropagation();
    }
    setValidated(true);
  }

  return (
    <div className='signup d-flex flex-column p-3 align-items-center min-vh-100'>
      <Form className='d-flex flex-column p-4 bg-light rounded-2 w-50'>
        <h1 className='text-center fw-bold'>Welcome</h1>
        <h3 id='signup-border' className='text-center d-flex flex-column align-self-center text-center fs-5 my-4'>Sign Up</h3>
        <Form.Group className='d-flex flex-column mb-3' controlId='formFirstname'>
          <Form.Label className='form-label fw-bold'>First Name</Form.Label>
          <Form.Control type='text' placeholder='John' size='sm' required value={userInfo.firstname} onChange={(firstname)=>setUserInfo({...userInfo, firstname:firstname})}/>
        </Form.Group>
        <Form.Group className='d-flex flex-column mb-3' controlId='formLastname'>
          <Form.Label className='form-label fw-bold'>Last Name</Form.Label>
          <Form.Control type='text' placeholder='Doe' size='sm' required value={userInfo.lastname} onChange={(lastname)=>setUserInfo({...userInfo, lastname:lastname})}/>
        </Form.Group>
        <Form.Group className='d-flex flex-column mb-3' controlId='formEmail'>
          <Form.Label className='form-label fw-bold'>Email</Form.Label>
          <Form.Control type='email' placeholder='abc@email.com' size='sm' required value={userInfo.email} onChange={(email)=>setUserInfo({...userInfo, email:email})}/>
        </Form.Group>
        <Form.Group className='d-flex flex-column mb-3' controlId='formPassword'>
          <Form.Label className='form-label password fw-bold'>Password</Form.Label>
          <Form.Control type='password' size='sm' required value={userInfo.password} onChange={(password)=>setUserInfo({...userInfo, password:password})}/>
        </Form.Group>
        <Form.Group className='d-flex flex-column mb-3' controlId='formConfirmPassword'>
          <Form.Label className='form-label password fw-bold'>Confirm Password</Form.Label>
          <Form.Control type='password' size='sm' required value={userInfo.confirmPassword} onChange={(confirm)=>setUserInfo({...userInfo, confirmPassword:confirm})}/>
        </Form.Group>
        <Button className='fw-bold mb-3' size='sm' type='submit'>Sign Up</Button>
        <Form.Group className='d-flex justify-content-sm-between justify-content-lg-end'>
          <Form.Text>Already have an account?</Form.Text>
          <Button size='sm' className='btn-sm ms-lg-2' variant='outline-primary' href='/signin'>Sign In</Button>
        </Form.Group>
      </Form>
    </div>
  )
}

export default SignUp
