import React from 'react'
import { Button, Form } from 'react-bootstrap'
import '../styles/Signin.css'


const Signin = () => {
  return (
    <Form className='d-flex flex-column bg-white p-2 rounded-2'>
      <h1 className='text-center fw-bold fs-3 my-1'>Welcome</h1>
      <h3 className='signin-border d-flex flex-column align-self-center text-center fs-5 my-3'>Sign In</h3>
      <Form.Group className='d-flex flex-column mb-3' controlId='formBasicIndexNumber'>
        <Form.Label className='form-label fw-bold'>Index Number</Form.Label>
        <Form.Control type='text' placeholder='Enter index number'/>
      </Form.Group>
      <Form.Group className='d-flex flex-column' controlId='formBasicPassword'>
        <Form.Label className='password fw-bold'>Password</Form.Label>
        <Form.Control type='password' placeholder='Enter password' />
      </Form.Group>
      <Form.Group className='d-flex justify-content-end m-2'>
        <a className='' href='/' role={'button'}>Forgot password?</a>
      </Form.Group>
      <Button className='mb-3 btn-sm fw-bold fs-7'>Sign In</Button>
      <Form.Group className='d-flex justify-content-between'>
        <p className='pe-3'>Don't have an account?</p>
        <Button className='btn-sm'>Sign Up</Button>
      </Form.Group>
    </Form>
    // <div id='signin-container'> 
    //   <h1>Welcome</h1>
    //   <h2>Sign In</h2>
    //   <div className='text-input-box'>
    //     <p>Index Number</p>
    //     <input type="text" placeholder='Enter your index number'/>
    //   </div>
    //   <div className='text-input-box'>
    //     <p>Password</p>
    //     <input type="text" placeholder='Enter your password here'/>
    //   </div>
    //   <a href="/" className='password-link'>Forgot password?</a>
    //   <button type="submit" className='signin-btn'>Sign In</button>
    //   <div className='pre-signup'>
    //     <p>Don't have an account?</p>
    //     <a href="/">Sign Up</a>
    //   </div>
    // </div>
  )
}

export default Signin
